mapper.insert:
insert into posts ( POSTNO, MEMBERID, POSTTITLE, POSTCONTENT, POSTDATE, POSTTYPE,
ORIGINALFILE, SAVEDFILE
) values ( postNoSeq.nextval, #{memberId}, #{postTitle}, #{postContent}, sysdate, #{postType},
#{originalFile}, #{savedFile}
)

mapper.update:
update posts set
originalFile = #{originalFile}, savedFile = #{savedFile},
postTitle = #{postTitle}, postContent = #{postContent}, postType = #{postType} where postNo = #{postNo}
