mapper.select:
SELECT memberEmail
FROM memberinfo
WHERE memberId = #{memberId}

mapper.update:
update MemberInfo
set emailchecked = 'y'
where memberId = #{memberId}

update MemberInfo
set photoname = #{photoname}
where memberId = #{memberId}

update MemberInfo
set savefile = #{savefile}
where memberId = #{memberId}

UPDATE
	memberinfo
SET	
	memberPwd   = #{memberPwd}
WHERE
	memberEmail = #{memberEmail}

UPDATE
	memberinfo
SET
	memberPwd = #{memberPwd}
WHERE
	memberId = #{memberId}
